(defn split [re s] (str/split s re))
(defn split-first [re s] (str/split s re 2))
(defn parse-int [s] (Integer/parseInt s))
(defn find-item [f coll] (->> coll (filter f) first))

(defn parse-things [things]
  (->> things
    (split #", ")
    (map #(split #": " %))
    (reduce (fn [m [thing amt]] (assoc m thing (parse-int amt))) {})))

(defn parse-sue [s]
  (->> s
    (split-first #": ")
    ((fn [[id things]] [
      (->> id (split #" ") last)
      (parse-things things)]))))

(def aunts (->> input str/split-lines (map parse-sue)))

(def valid-things {
  "children" 3
  "cats" 7
  "samoyeds" 2
  "pomeranians" 3
  "akitas" 0
  "vizslas" 0
  "goldfish" 5
  "trees" 3
  "cars" 2
  "perfumes" 1})

(defn valid-p1 [[_ things]]
  (->> things
    keys
    (every? #(= (things %) (valid-things %)))))

(defn valid-p2 [[_ things]]
  (and
    (< (valid-things "cats")        (get things "cats"  100))
    (< (valid-things "trees")       (get things "trees" 100))
    (> (valid-things "pomeranians") (get things "pomeranians" 0))
    (> (valid-things "goldfish")    (get things "goldfish" 0))
    (valid-p1 ["" (dissoc things "cats" "trees" "pomeranians" "goldfish")])))

(->> aunts (find-item valid-p1) first (println "Part one:"))
(->> aunts (find-item valid-p2) first (println "Part one:"))
