fn part_one() -> i64 {
  let (mut x, mut y, mut r) = (0, 0, 90);
  for (d, n) in &INPUT {
    match d {
      'N' => y += n,
      'S' => y -= n,
      'E' => x += n,
      'W' => x -= n,
      'L' => r -= n,
      'R' => r += n,
      'F' => match r {
        0   => y += n,
        90  => x += n,
        180 => y -= n,
        270 => x -= n,
        _ => unreachable!(),
      }
      _ => unreachable!(),
    }
    r = (r + 360) % 360; // to keep it in [0,360)
  }
  x.abs() + y.abs()
}

fn rot(x: i64, y: i64, d: i64) -> (i64,i64) {
  match d {
    90  => (-y,  x),
    180 => (-x, -y),
    270 => ( y, -x),
    _ => unreachable!(),
  }
}

fn part_two() -> i64 {
  let (mut x, mut y) = (10, 1); // waypoint
  let (mut i, mut j) = (0, 0);  // ship
  for &(d,n) in &INPUT {
    match d {
      'N' => y += n,
      'S' => y -= n,
      'E' => x += n,
      'W' => x -= n,
      'L' => {
        let (a,b) = rot(x, y, n);
        x = a;
        y = b;
      }
      'R' => {
        let (a,b) = rot(x, y, 360 - n);
        x = a;
        y = b;
      }
      'F' => {
        i += x * n;
        j += y * n;
      }
      _ => unreachable!(),
    }
  }
  i.abs() + j.abs()
}

fn main() {
  let now = Instant::now();
  println!("Part one: {}", part_one());
  println!("Part two: {}", part_two());
  println!("Time: {}ms", now.elapsed().as_millis());
}
